import React, { useEffect, useState } from 'react';
import axios from 'axios';
import './main.css';

function Main() {
  const [permissionStatus, setPermissionStatus] = useState('checking'); // 'checking', 'granted', 'denied'
  const [location, setLocation] = useState(null);
  const [error, setError] = useState(null);

  const checkLocationPermission = () => {
    if (!("geolocation" in navigator)) {
      setError("Geolocation is not supported by your browser.");
      setPermissionStatus('denied');
      return;
    }

    navigator.permissions.query({ name: 'geolocation' })
      .then(permissionStatus => {
        setPermissionStatus(permissionStatus.state);
        
        permissionStatus.onchange = () => {
          setPermissionStatus(permissionStatus.state);
        };
      });
  };

  const getLocation = () => {
    setError(null);
    navigator.geolocation.getCurrentPosition(
      (position) => {
        const { latitude, longitude } = position.coords;
        setLocation({ latitude, longitude });
        setPermissionStatus('granted');

        axios.post("http://localhost:5000/api/location", {
          latitude,
          longitude
        }).then((res) => {
          console.log("‚úÖ Location stored:", res.data);
        }).catch((err) => {
          console.error("‚ùå Error storing location:", err);
          setError("Failed to store location. Please try again.");
        });
      },
      (error) => {
        console.error("‚ùå Permission denied or error:", error);
        setPermissionStatus('denied');
        setError("Location access denied. Please allow location access to continue.");
      }
    );
  };

  const requestPermission = () => {
    getLocation();
  };

  useEffect(() => {
    checkLocationPermission();
  }, []);

  return (
    <div className="location-container">
      {permissionStatus === 'checking' && (
        <div className="status-message">
          <h2>üìç Checking location permission...</h2>
        </div>
      )}

      {permissionStatus === 'granted' && location && (
        <div className="status-message success">
          <h2>‚úÖ Location access granted!</h2>
          <p>Latitude: {location.latitude}</p>
          <p>Longitude: {location.longitude}</p>
        </div>
      )}

      {permissionStatus === 'denied' && (
        <div className="status-message error">
          <h2>‚ùå Location access denied</h2>
          <p>{error || "Please allow location access to continue."}</p>
          <button 
            className="permission-button"
            onClick={requestPermission}
          >
            Allow Location Access
          </button>
        </div>
      )}

      {error && permissionStatus !== 'denied' && (
        <div className="error-message">
          {error}
        </div>
      )}
    </div>
  );
}

export default Main;
